import React from 'react';
import ReactDOM from 'react-dom';
import './style/index.css';
//import './style/_buttons.scss';
import ImageUploader from 'react-images-upload';
import Jimp from 'jimp/es';

var jimp = require('jimp');

var colors = {
  black: '#000000',
  onyx: '#333538',
  charcoal: '#5f6468',
  concrete: '#97a0a7',
  quicksilver: '#e4e5e6',
  bleach: '#ffffff',
  rich_brown: '#372820',
  mushroom: '#716257',
  sand: '#bab3a0',
  natural: '#f9f4ec',
  navy: '#081e2c',
  ultramarine: '#29217d',
  lagoon: '#003e66',
