  pine: '#2b4423',
  kelly: '#21772f',
  jade: '#14b25a',
  turf: '#60933b',
  lime: '#a0ce3e',
  goldenrod: '#d5d03a',
  yellow: '#fff71b',
  sunflower: '#ffc938',
  carmel: '#d8a15e',
  clementine: '#ff9a23',
  living_coral: '#fb686d',
  persimmon: '#fd5130',
  red_orange: '#db1d3c',
  red: '#c50f2d',
  burnt_orange: '#a84c28',
  rust: '#763742',
  maroon: '#581c32',
  deep_pink: '#c40876',
  hibiscus: '#de4b9b',
  blush: '#fdaeca',
  peach: '#f7bfab',
  lavender: '#a08dd6',
  iris: '#77479f',
  purple: '#5d1e5c',
  royal_purple: '#401e72',
  moose: '#653c2c',
  violet: '#6753b2',
  green_apple: '#76c727',
  carrot: '#fd673e',
  lapis: '#3c2f8f',
  parrot: '#2fd078',
  french_blue: '#638ee4',
};

var nearestColor = require('nearest-color').from(colors);


//initialize the default image
const src = require("./color-swatch.bmp");
//janky way around CORS policy for getting from dropbox link, pass through cors-anywhere server
//var dropboxURL = "0"
//var imgFinal = ("https://cors-anywhere.herokuapp.com/" + dropboxURL.slice(0, -1) + "1");

//-------------------------------------------------------------
//Main classes for displaying the image we are editing
//as well as the resulting images after edits
//-------------------------------------------------------------
class DisplayLogoImg extends React.Component{
  render(){
    return(
      <img className="workImage" src={this.props.picture} alt="Uploaded logo" />
    )
  }
}

//-------------------------------------------------------------
//Custom classes for each button, passes state up
//TODO:   Merge all of one type into a single class? [buttons, forms, etc]
//        Transfer classes into seperate file and import it
//-------------------------------------------------------------

class Linkform extends React.Component {
  render() {
    return (
      <form>
        <label>
          Dropbox Link:
          <input type="text" onChange={this.props.onChange} />
        </label>
      </form>
    );
  }
}

class DebugButton extends React.Component{
  render(){
    return(
      <button onClick={() => this.props.onClick()}>Debug</button>
    )
  }
}

class DownloadButton extends React.Component{
  render(){
    return(
      <button onClick={() => this.props.onClick()}>Download BMP</button>
    )
  }
}

class ConvertButton extends React.Component{
  render(){
    return(
      <button onClick={() => this.props.onClick()}>Convert file to BMP</button>
    )
  }
}

//-------------------------------------------------------------
//Main editor app class, contains most info in state
//-------------------------------------------------------------
class Editor extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      //CURRENTLY NOT USED: values for pixelization and posterize alteration
      pixelValue: 0,
      thresholdValue: 0,
      //value that holds the path to default image, then the base64 data of render
      //base64 used by Jimp, since it is a web-based app. Local files caused issues.
      picture: src,
      //preset and chassis for bitmap generation
      preset: 0,
      chassis: 0,
      //Locations for crop rectangle
      //TODO: Change names to something less global
      width: 100,
      height: 100,
      top: 20,
      left: 20,
      rotateAngle: 0,
      //Scale values for scale operation
      scaleX: 0,
      scaleY: 0,
      //X Y coordinates of logo on Bitmap
      xCoord: 0,
      yCoord: 0,
      //logo image width and height, mainly for displaying and stretch functionality
      logoWidth: 0,
      logoHeight: 0,
      //show the crop tool or not
      showCrop: 0
    };
    this.myRef = React.createRef();
    //TODO: Bind these functions in their declaration, similar to handleX functions
    this.onDrop = this.onDrop.bind(this);
    this.onLinkChange = this.onLinkChange.bind(this);
    this.jimpScale = this.jimpScale.bind(this);
    this.jimpResize = this.jimpResize.bind(this);
    this.jimpPixelate = this.jimpPixelate.bind(this);
    this.jimpUpdateSize = this.jimpUpdateSize.bind(this);
  }

  //-------------------------------------------------------------
  //Functions to handle events when different buttons are clicked
  //or values are changed
  //-------------------------------------------------------------
  handleDebugButtonClick = (i) => {
      var posterizeValue = 2;
      var pixelateValue = 10;
      var scale = false;
      this.jimpLogoCreation(this.state.picture, pixelateValue, posterizeValue, scale);
      //console.log(nearestColor('#f9f4eb'));
  }

  handleConvertButtonClick = (i) => {
      this.jimpConvert(this.state.picture);
  }

  handleDownloadButtonClick = (i) => {
    console.log("Download Button Clicked");
  }

  //picture is an array passed from image uploader
  //pull most recently uploaded picture from current session
  //TODO: reformat these functions into handleDrop and handleLinkChange
  onDrop(picture){
    //TODO: Insert function calls directly here to auto work on image
    var localImg = URL.createObjectURL(picture[picture.length - 1]);

    this.jimpLogoCreation(localImg);

    //Set state for display
    this.setState({
      picture: URL.createObjectURL(picture[picture.length - 1]),
    });
    //this.jimpScale(localImg, 200, 200);
    //This is very janky, waits 1 second before updating while picture state is being changed
    //TODO: Figure out some sort of wait or confirmation,
    //      this will introduce problems with varying connection speeds
    /*
    setTimeout(function () {
      this.jimpUpdateSize(this.state.picture);
    }.bind(this), 1000);
    */
  }

  onLinkChange(event){
    //janky way around CORS policy for getting from dropbox link, pass through cors-anywhere server
    this.setState({
      picture: "https://cors-anywhere.herokuapp.com/" + event.target.value.slice(0, -1) + "1"
    });
  }

  //-------------------------------------------------------------
  //render functions for each visible element
  //TODO: combine everything in the same div into one render function
  //like buttons for control panel and such
  //-------------------------------------------------------------

  renderLogoImage(){
    return (
      <div>
        <DisplayLogoImg
          picture={this.state.picture}
        />
      </div>
    )
  }

  renderDebugButton(i){
    return(
      <DebugButton
        onClick={this.handleDebugButtonClick}
      />
    )
  }

  renderDownloadButton(i){
    return(
      <DownloadButton
        onClick={this.handleDownloadButtonClick}
      />
    )
  }

  renderConvertButton(i){
    return(
      <ConvertButton
        onClick={this.handleConvertButtonClick}
      />
    )
  }

  renderImageUploader(){
    return(
      <ImageUploader
        withIcon={true}
        buttonText='Choose image'
        label="Max file size: 5mb, Filetypes accepted: .bmp | .jpg | .png"
        fileContainerStyle={{backgroundColor: "grey"}}
        singleImage={true}
        onChange={this.onDrop}
        imgExtension={['.jpg', '.gif', '.png', '.bmp']}
        maxFileSize={5242880}
      />
    )
  }

  renderLinkForm(){
    return(
      <Linkform onChange={this.onLinkChange} onSubmit={this.onLinkSubmit}/>
    )
  }


  //-------------------------------------------------------------
  //Functions to do the actual editing on the image
  //-------------------------------------------------------------
  jimpConvert(path, x, y){
