  jimpUpdateSize(path){
    var localX = 0;
    var localY = 0;
    jimp.read(path, function(err, image) {
      localX = image.bitmap.width;
      localY = image.bitmap.height;
      this.setState({
        logoWidth: localX,
        logoHeight: localY,
      });
    }.bind(this));
  }

