      //Array of output pictures
      output: [],
      dropboxLink: "",
      scaleX: 0,
      scaleY: 0,
      imagesLoaded: 0,
      stretch: false,
    };
    this.myRef = React.createRef();
    //TODO: Bind these functions in their declaration, similar to handleX functions
    this.onDrop = this.onDrop.bind(this);
    this.onScaleFormChangeX = this.onScaleFormChangeX.bind(this);
    this.onScaleFormChangeY = this.onScaleFormChangeY.bind(this);
    this.onStretchFormChange = this.onStretchFormChange.bind(this);
  }

  componentDidUpdate(prevProps, prevState){
    if (prevState.output !== this.state.output){
      //console.log("output state has changed: " + this.state.output[0]);
    }
  }

  //-------------------------------------------------------------
  //Functions to handle events when different buttons are clicked
  //or values are changed
  //-------------------------------------------------------------
  handleProcessButtonClick = () => {
      //Format: posterize[bool], posterize value, scale[bool], scaleX, scaleY, stretch[bool], greyscale[bool], numColors
      //TODO: format better, into a function or something
      //      these function calls end up being "jumbled", i think due to the async setState,
      //      so it is difficult to know which is what output in the array

      //Scale : Color Correction
      this.jimpLogoCreation(this.state.output[0], false, 0, true, this.state.scaleX, this.state.scaleY, this.state.stretch);
      //Scale : Greyscale : Color Correction
      this.jimpLogoCreation(this.state.output[0], false, 0, true, this.state.scaleX, this.state.scaleY, this.state.stretch, true);
      //Scale : Color Correction (Reduction)
      this.jimpLogoCreation(this.state.output[0], false, 0, true, this.state.scaleX, this.state.scaleY, this.state.stretch, false, 5);
      this.jimpLogoCreation(this.state.output[0], false, 0, true, this.state.scaleX, this.state.scaleY, this.state.stretch, false, 4);
      this.jimpLogoCreation(this.state.output[0], false, 0, true, this.state.scaleX, this.state.scaleY, this.state.stretch, false, 3);
      this.jimpLogoCreation(this.state.output[0], false, 0, true, this.state.scaleX, this.state.scaleY, this.state.stretch, false, 2);
      //Scale : Posterize : Color Correction
      this.jimpLogoCreation(this.state.output[0], true, 7, true, this.state.scaleX, this.state.scaleY, this.state.stretch);
      this.jimpLogoCreation(this.state.output[0], true, 5, true, this.state.scaleX, this.state.scaleY, this.state.stretch);
      this.jimpLogoCreation(this.state.output[0], true, 3, true, this.state.scaleX, this.state.scaleY, this.state.stretch);
      this.jimpLogoCreation(this.state.output[0], true, 2, true, this.state.scaleX, this.state.scaleY, this.state.stretch);
      //Scale : Posterize : Color Correction (Reduction)
      this.jimpLogoCreation(this.state.output[0], true, 7, true, this.state.scaleX, this.state.scaleY, this.state.stretch, false, 5);
      this.jimpLogoCreation(this.state.output[0], true, 7, true, this.state.scaleX, this.state.scaleY, this.state.stretch, false, 4);
      this.jimpLogoCreation(this.state.output[0], true, 7, true, this.state.scaleX, this.state.scaleY, this.state.stretch, false, 3);
      this.jimpLogoCreation(this.state.output[0], true, 7, true, this.state.scaleX, this.state.scaleY, this.state.stretch, false, 2);
      this.jimpLogoCreation(this.state.output[0], true, 5, true, this.state.scaleX, this.state.scaleY, this.state.stretch, false, 5);
      this.jimpLogoCreation(this.state.output[0], true, 5, true, this.state.scaleX, this.state.scaleY, this.state.stretch, false, 4);
      this.jimpLogoCreation(this.state.output[0], true, 5, true, this.state.scaleX, this.state.scaleY, this.state.stretch, false, 4);
      this.jimpLogoCreation(this.state.output[0], true, 5, true, this.state.scaleX, this.state.scaleY, this.state.stretch, false, 3);
      this.jimpLogoCreation(this.state.output[0], true, 3, true, this.state.scaleX, this.state.scaleY, this.state.stretch, false, 5);
      this.jimpLogoCreation(this.state.output[0], true, 3, true, this.state.scaleX, this.state.scaleY, this.state.stretch, false, 4);
      this.jimpLogoCreation(this.state.output[0], true, 3, true, this.state.scaleX, this.state.scaleY, this.state.stretch, false, 3);
      this.jimpLogoCreation(this.state.output[0], true, 3, true, this.state.scaleX, this.state.scaleY, this.state.stretch, false, 3);
      this.jimpLogoCreation(this.state.output[0], true, 2, true, this.state.scaleX, this.state.scaleY, this.state.stretch, false, 5);
      this.jimpLogoCreation(this.state.output[0], true, 2, true, this.state.scaleX, this.state.scaleY, this.state.stretch, false, 4);
      this.jimpLogoCreation(this.state.output[0], true, 2, true, this.state.scaleX, this.state.scaleY, this.state.stretch, false, 3);
      this.jimpLogoCreation(this.state.output[0], true, 2, true, this.state.scaleX, this.state.scaleY, this.state.stretch, false, 2);
  }

  handleColorChangeButtonClick = (i) => {
    //NOTE - THIS RETURNS THE VALUE, NO NEED FOR .VALUE
    this.jimpLogoCreation(this.state.output[0], false, 0, false, 0, 0, false);

  }

  handleSubmitButtonClick = (i) => {
    console.log("Submit Button Clicked");
    this.setState({
      output: [this.state.dropboxLink],
    });
  }

  handleDownloadButtonClick = (i) => {
    console.log("Download Button Clicked");
  }

  handleLinkChange = (i) => {
    this.setState({
      dropboxLink: "https://cors-anywhere.herokuapp.com/" + i.target.value.slice(0, -1) + "1",
    });
  }

  //picture is an array passed from image uploader
  //pull most recently uploaded picture from current session
  //TODO: reformat these functions into handleDrop and handleLinkChange
  onDrop(picture){
    //TODO: Insert function calls directly here to auto work on image
    var localImg = URL.createObjectURL(picture[picture.length - 1]);
    //Set state for display
    this.setState({
      output: [URL.createObjectURL(picture[picture.length - 1])],
    });
  }

  onScaleFormChangeX(event){
    //form takes in a string, need to parse into an Int for use in Jimp
    this.setState({
      scaleX: parseInt(event.target.value, 10)
    });
  }

  onScaleFormChangeY(event){
    //form takes in a string, need to parse into an Int for use in Jimp
    this.setState({
      scaleY: parseInt(event.target.value, 10)
    });
  }

  onStretchFormChange(event){
    //console.log(event.target.checked);
    this.setState({
      stretch: event.target.checked,
    });
  }

  //-------------------------------------------------------------
  //render functions for each visible element
  //TODO: combine everything in the same div into one render function
  //like buttons for control panel and such
  //-------------------------------------------------------------

  renderLogoImage(){
